    Filetypes[0] = "bmp";
    Filetypes[1] = "jpg";
    Filetypes[2] = "gif";
    Filetypes[3] = "tiff";
    Filetypes[4] = "png";
    SelectedMimetype = 0;
  ]
]

TakeSnapshots [
  {***** Snapshot the window for each mime type *****}
  If SelectedMimeType < #NUM_TYPES;
  [
    WindowSnapshot(Concat(PathString, "\Lighthouse.", FileTypes[SelectedMimetype]),
                   Self(),
                   Mimetypes[SelectedMimetype]);
    SelectedMimetype++;
  ]

